import React from 'react';
import ReactDOM from 'react-dom';
import MyContext from './my_context.js';
import MarketWidget from './widget.js';




const root = {
	display: 'flex',
	flexDirection: 'column'
}

const api = '/get-products'
let calculate = (update_data, api_dict) => {
	console.log(update_data)
	const listItems = update_data.map((item) => {
		var code_name = item.s
		return(
			<li><span>{code_name}</span><span>{Math.abs(item.c - api_dict.code_name)/(api_dict.code_name)}</span></li>
		)
	})

	return (
		<ul> { listItems } </ul>
	)
}

let sort = (list) => {
	var temp_dict = {}

	list.forEach(
		i => {
			temp_dict[i.s] = i.o
		}
	)
	return temp_dict
}

class Index extends React.Component {
    constructor(props) {
        super(props)
        this.myRef = React.createRef();
        this.state = {
            market_data: null,
            loaded: false
        }
    }

    componentDidMount() {
        fetch(api)
        .then(
            res => {
                return res.json()
            }
        )
        .then((output) => {
            var api_data = sort(output.data)
            this.setState({
                market_data: api_data,
                loaded: true
            })
            this.myRef.current.socket_setup()
        })
    }

	render() {
        if (this.state.loaded) {
            return (
                <MyContext.Provider
                    value = {{
                        data: this.state.market_data,
                        change: calculate
                    }}
                >
					<div style = { root }>
						<MarketWidget ref={this.myRef} />
					</div>
                </MyContext.Provider>
            );
        }
        else {
            return (
				<div style = { root }>
					<h1> Loading ... </h1>
				</div>
            );
        }			
	}
}


// ========================================


ReactDOM.render(
	<Index />,
	document.getElementById('root'),	
);
