class Index extends React.Component {
    constructor(props) {
        super(props)
        this.myRef = React.createRef();
		// this.callback_Ref = null;
		// this.setRef = element => {
		// 	this.callback_Ref = element;
		// }
        this.state = {
            market_data: [],
            loaded: false
        }
    }

    componentDidMount() {
        fetch(api)
        .then(
            res => {
                return res.json()
            }
        )
        .then((output) => {
            var api_data = output.data
            // var num = api_data.length
            // var data_list = api_data
            // var data_dict = {}
            // var data = recur_foo(num, data_list, data_dict)
            this.setState({
                market_data: api_data,
                loaded: true
            })
            this.myRef.current.socket_setup()
        })
    }

	render() {
        if (this.state.loaded) {
            return (
                <MyContext.Provider
                    value = {{
                        data: this.state.market_data
                    }}
                >
					<div style = { root }>
						<MarketWidget ref={this.myRef} />
					</div>
                </MyContext.Provider>
            );
        }
        else {
            return (
				<div style = { root }>
					<h1> Loading ... </h1>
				</div>
            );
        }			
	}
}


