}

const api = '/get-products'

let calculate = (update_data, api_dict) => {
	var list = JSON.parse(update_data)
    const listItems = list.data.map((item) => 
        <li>
            <span>{item.s} ==> </span>
            <span>Change: {Math.abs(item.c - api_dict[item.s])/api_dict[item.s]}</span>
