import React from 'react';
import ReactDOM from 'react-dom';
import MarketWidget from './widget.js';




const root_base = {
	display: 'flex',
	justifyContent: 'center',
};

const base = {
	padding: '1vh',
	width: '40%',
	height: '80vh',
	backgroundColor: 'red',
};

const url = 'wss://stream.binance.com/stream?streams=!miniTicker@arr'
let isConnected = false


let websocket_clickhandler = () => {
	if (!isConnected) {
		let channel = new WebSocket(url)
		channel.onopen = function(event, isConnected) {
			isConnected = true
		}
		channel.onmessage = function(event) {
			console.log(event.data)
		}		
		window.alert(isConnected)
	}
}


class Base extends React.Component {

	// componentDidMount() {
	// 	channel.onmessage = e => {
	// 		console.log(e.data)
	// 	}
	// 	channel.onerror = error => {
	// 		console.log(`WebSocket error: ${error}`)
	// 	}
	// }

	render() {
		return (
			<div style = { root_base }>
				<div style = { base }>
					<MarketWidget />
				</div>
				<button onClick={websocket_clickhandler}/>
			</div>
		);
	}
}


// ========================================


ReactDOM.render(
	<Base />,
	document.getElementById('root'),	
);
