import React from 'react';
import MyContext from './my_context.js';



const api = 'https://www.binance.com/exchange-api/v1/public/asset-service/product/get-products'

class MyProvider extends React.Component {
    state = {
        market_data: []
    }

    componentDidMount() {

        fetch(api
            ).then(res => res.json())
        .then((res) => console.log(res)).catch(console.log('======= error ======='))

        // fetch(api, {mode: 'no-cors'}).then(res => res.json())
        // .then((data) => {
        //     this.setState({ market_data: data })
        // })
        // .catch(console.log)
    }

    render() {
        return (
            <MyContext.Provider
                value={{
                    market_data: this.state.market_data,
                }}
            >
                {this.props.children}
            </MyContext.Provider>
        );
    }
}

export default MyProvider;
