import React from 'react';
import ReactDOM from 'react-dom';
import MarketWidget from './widget.js';




const root_base = {
	display: 'flex',
	justifyContent: 'center',
};

const base = {
	padding: '1vh',
	width: '40%',
	height: '80vh',
	backgroundColor: 'red',
};

const url = 'wss://stream.binance.com/stream?streams=!miniTicker@arr'
let channel = new WebSocket(url)


let isConnected = false
channel.onclose = function(event) {
	isConnected = false
}
channel.onopen = function(event) {
	isConnected = true
}
channel.onmessage = function(event) {
	console.log(event.data)
}


const load_websocket = (isConnected) => {
	if (!isConnected) {
		window.alert('start')
		return new Promise((resolve, reject) => {
			channel = new WebSocket(url)
			channel.onmessage = function(event) {
				console.log(event.data)
			}
		}).then(() => {
			if (WebSocket.readyState !== WebSocket.OPEN) {
				return false
			}
			else {
				return true
			}
		})
	}
	else {
		window.alert('close')
		return new Promise((resolve, reject) => {
			channel.close()
		}).then(() => {
			if (WebSocket.readyState !== WebSocket.CLOSED) {
				return false
			}
			else {
				return true
			}
		})
	}
}


const websocket_clickhandler = () => {
	let result = load_websocket(isConnected)
	if (result) {
		window.alert('done!')
	}
	else {
		window.alert('error!')
	}
}


class Base extends React.Component {

	// componentDidMount() {
	// 	channel.onmessage = e => {
	// 		console.log(e.data)
	// 	}
	// 	channel.onerror = error => {
	// 		console.log(`WebSocket error: ${error}`)
	// 	}
	// }

	render() {
		return (
			<div style = { root_base }>
				<div style = { base }>
					<MarketWidget />
				</div>
				<button onClick={websocket_clickhandler}/>
			</div>
		);
	}
}


// ========================================


ReactDOM.render(
	<Base />,
	document.getElementById('root'),	
);
