import React from 'react';
import MyContext from './my_context.js';



const api = '/get-products'


let recur_foo = (num, data_list, data_dict) => {
    if (num <= 0) {
        return data_dict
    }

    var temp_l = []
    var key = data_list[num-1]['b']

    data_list.map(
        i => {
            if (i.pm === key) {
                temp_l.push(i.b)
            }
        }
    )
    data_dict[key] = temp_l
    num -= 1
    return recur_foo(num, data_list, data_dict)
}

class MyProvider extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            market_structure: [],
            market_data: [],
        };
    }

    componentDidMount() {
        fetch(api)
        .then(
            res => {
                return res.json()
            }
        )
        .then((output) => {
            var api_data = output.data
            var num = api_data.length
            var data_list = api_data
            var data_dict = {}
            var data = recur_foo(num, data_list, data_dict)
            this.setState({market_structure: data, market_data: api_data})
        })
    }

    render() {
        return (
            <MyContext.Provider
                value={{
                    market_structure: this.state.market_structure, 
                    market_data: this.state.market_data
                }}
            >
                {console.log(this.state.market_data)}
                {this.props.children}
            </MyContext.Provider>
        );
    }
}

export default MyProvider;
