import React from 'react'
import Widget from './stock_widget'



class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            loaded: false,
            api_data: null
        }
    }

    componentDidMount() {
        const api = '/get-products'

        fetch(api)
        .then(
            res => {
                return res.json()
            }
        )
        .then((output) => {
            if(output.data) {
                this.setState({
                    loaded: true,
                    api_data: output.data
                })
            }
        })
    }

    render() {
        if (this.state.loaded) {
            return (
                <div>
                    <Widget />
                </div>
            );
        }
        else {
            return (
                <div>
                    <h1> Loading ... </h1>
                </div>
            );
        }
    }
}

export default App;
