import React from 'react';
import ReactDOM from 'react-dom';
import MarketWidget from './widget.js';




const root_base = {
	display: 'flex',
	justifyContent: 'center',
};

const base = {
	padding: '1vh',
	width: '40%',
	height: '80vh',
	backgroundColor: 'red',
};

const url = 'wss://stream.binance.com/stream?streams=!miniTicker@arr'
const channel = new WebSocket(url)


let isConnected = false
channel.onclose = function(event) {
	isConnected = false
}
channel.onopen = function(event) {
	isConnected = true
}


const load_websocket = (isConnected) => {
	if (!isConnected) {
		return new Promise((resolve, reject) => {
			channel.open().then(() => {
				if (WebSocket.readyState !== 1) {
					reject(false)
				}
				else {
					resolve(true)
				}
			})
		})
	}
	else {
		return new Promise( async (resolve, reject) => {
			channel.close().then(() => {
				if (WebSocket.readyState !== 3) {
					reject(false)
				}
				else {
					resolve(true)
				}
			})
		})
	}
}


const websocket_clickhandler = () => {
	load_websocket(isConnected)
	.then((result) => {
			if (result) {
				console.alert('done!')
			}
			else {
				console.alert('error!')
			}
		}
	)
}


class Base extends React.Component {

	componentDidMount() {
		channel.onmessage = e => {
			console.log(e.data)
		}
		channel.onerror = error => {
			console.log(`WebSocket error: ${error}`)
		}
	}

	render() {
		return (
			<div style = { root_base }>
				<div style = { base }>
					<MarketWidget />
				</div>
				<button onClick={websocket_clickhandler}/>
			</div>
		);
	}
}


// ========================================


ReactDOM.render(
	<Base />,
	document.getElementById('root'),	
);
