import React from 'react';
import ReactDOM from 'react-dom';
import MarketWidget from './widget.js';




const root_base = {
	display: 'flex',
	justifyContent: 'center',
};

const base = {
	padding: '1vh',
	width: '40%',
	height: '80vh',
	backgroundColor: 'red',
};

const url = 'wss://stream.binance.com/stream?streams=!miniTicker@arr'
const connection = new WebSocket(url)
const button = (con) => {
	if (con.readyState === WebSocket.OPEN) {
		con.close()
	}
	else if (con.readyState === WebSocket.CLOSE) {
		con.open()
	}
}


class Base extends React.Component {
	constructor(props) {
		super(props);
	}

	componentDidMount() {
		connection.onopen = () => {
			alert('open')
		}
		connection.onclose = () => {
			alert('close')
		}
		connection.onmessage = e => {
			console.log(e.data)
		}
		connection.onerror = error => {
			console.log(`WebSocket error: ${error}`)
		}
	}

	close_socket() {
		if (!this.state.pause) {
			connection.close()
		}
		else {

		}
	}

	render() {
		return (
			<div style = { root_base }>
				<div style = { base }>
					<MarketWidget />
				</div>
				<button con={connection}/>
			</div>
		);
	}
}


// ========================================


ReactDOM.render(
	<Base />,
	document.getElementById('root'),	
);
