import React from 'react';
import MyContext from './my_context.js';



//const api = 'https://www.binance.com/exchange-api/v1/public/asset-service/product/get-products'
const api = 'http://jsonplaceholder.typicode.com/users'

class MyProvider extends React.Component {
    state = {
        market_data: []
    }

    componentDidMount() {
        fetch(api)
        .then(res => res.json())
        .then((data) => {
            console.log(data)
        })
    }

    // componentDidMount() {
    //     fetch(api)
    //     .then(res => res.json())
    //     .then((data) => {
    //         this.setState({ contacts: data })
    //     })
    //     .catch(console.log)
    // }


    render() {
        return (
            <MyContext.Provider
                value={{
                    market_data: this.state.market_data,
                }}
            >
                {this.props.children}
            </MyContext.Provider>
        );
    }
}

export default MyProvider;
